use crate::*;
use radix_engine_common::data::scrypto::ScryptoValue;
use sbor::rust::fmt::Debug;
use sbor::rust::prelude::*;
use sbor::Value;

pub const METADATA_BLUEPRINT: &str = "Metadata";

pub const METADATA_STRING_TYPE: u8 = 0u8;

pub struct MetadataValues;

impl MetadataValues {
    pub fn string(value: String) -> ScryptoValue {
        ScryptoValue::Enum {
            discriminator: METADATA_STRING_TYPE,
            fields: vec![
                Value::String { value }
            ],
        }
    }

    pub fn get_string(value: ScryptoValue) -> Result<String, MetadataError> {
        match value {
            ScryptoValue::Enum {
                     discriminator,
                     fields,
                 } if discriminator == METADATA_STRING_TYPE => {
                match fields.into_iter().next().unwrap() {
                    Value::String { value, } => Ok(value),
                    _ => panic!("Unexpected"),
                }
            },
            _ => Err(MetadataError::NotAString),
        }
    }
}

#[derive(Debug, Clone, Eq, PartialEq, ScryptoSbor)]
pub enum MetadataError {
    NotAString,
    EmptyEntry,
}

pub const METADATA_CREATE_IDENT: &str = "create";

#[derive(
    Debug, Clone, Eq, PartialEq, ScryptoSbor, ManifestCategorize, ManifestEncode, ManifestDecode,
)]
pub struct MetadataCreateInput {}

