use crate::api::types::*;
use crate::blueprints::resource::Bucket;
use crate::*;
use sbor::rust::fmt::Debug;

#[derive(Debug, Clone, Eq, PartialEq, ScryptoCategorize, ScryptoEncode, ScryptoDecode)]
pub struct ComponentSetRoyaltyConfigInvocation {
    /// TODO: change to component id, after `borrow_component` returns component id
    pub receiver: RENodeId,
    pub royalty_config: RoyaltyConfig,
}

impl Invocation for ComponentSetRoyaltyConfigInvocation {
    type Output = ();

    fn fn_identifier(&self) -> FnIdentifier {
        FnIdentifier::Native(NativeFn::ComponentRoyalty(
            ComponentRoyaltyFn::SetRoyaltyConfig,
        ))
    }
}

impl SerializableInvocation for ComponentSetRoyaltyConfigInvocation {
    type ScryptoOutput = ();

    fn native_fn() -> NativeFn {
        NativeFn::ComponentRoyalty(ComponentRoyaltyFn::SetRoyaltyConfig)
    }
}

impl Into<CallTableInvocation> for ComponentSetRoyaltyConfigInvocation {
    fn into(self) -> CallTableInvocation {
        NativeInvocation::ComponentRoyalty(ComponentRoyaltyInvocation::SetRoyaltyConfig(self))
            .into()
    }
}

