use crate::types::*;
use radix_engine_interface::blueprints::resource::{AccessRule, AuthorityEntry, MethodKey};

#[derive(ScryptoSbor, ScryptoEvent)]
pub enum SetRuleEvent {
    Method { key: MethodKey, rule: AuthorityEntry },
    Group { name: String, rule: AuthorityEntry },
}

#[derive(ScryptoSbor, ScryptoEvent)]
pub enum SetMutabilityEvent {
    Method {
        key: MethodKey,
        mutability: AuthorityEntry,
    },
    Group {
        name: String,
        mutability: AccessRule,
    },
}
