use crate::types::*;
use radix_engine_interface::blueprints::resource::AccessRule;

#[derive(ScryptoSbor, ScryptoEvent)]
pub struct UpdateMethodEvent {
    pub method_key: MethodKey,
    pub permission: Option<MethodPermission>,
    pub mutability: Option<RoleList>,
}

#[derive(ScryptoSbor, ScryptoEvent)]
pub struct DefineRoleEvent {
    pub role_key: RoleKey,
    pub rule: AccessRule,
}

#[derive(ScryptoSbor, ScryptoEvent)]
pub struct SetRoleMutabilityEvent {
    pub role_key: RoleKey,
    pub mutability: RoleList,
}
