use native_sdk::{LegacyDescribe, ScryptoSbor};
use radix_engine_interface::api::types::String;
use radix_engine_interface::blueprints::resource::{AccessRule, AccessRuleEntry, AccessRuleKey};

#[derive(ScryptoSbor, LegacyDescribe)]
pub enum SetRuleEvent {
    Method {
        index: u32,
        key: AccessRuleKey,
        rule: AccessRuleEntry,
    },
    Group {
        index: u32,
        name: String,
        rule: AccessRule,
    },
}

#[derive(ScryptoSbor, LegacyDescribe)]
pub enum SetMutabilityEvent {
    Method {
        index: u32,
        key: AccessRuleKey,
        mutability: AccessRule,
    },
    Group {
        index: u32,
        name: String,
        mutability: AccessRule,
    },
}
