use crate::types::*;
use radix_engine_interface::blueprints::resource::{AccessRule, AccessRuleEntry, MethodKey};

#[derive(ScryptoSbor)]
pub enum SetRuleEvent {
    Method {
        index: u32,
        key: MethodKey,
        rule: AccessRuleEntry,
    },
    Group {
        index: u32,
        name: String,
        rule: AccessRule,
    },
}

#[derive(ScryptoSbor)]
pub enum SetMutabilityEvent {
    Method {
        index: u32,
        key: MethodKey,
        mutability: AccessRule,
    },
    Group {
        index: u32,
        name: String,
        mutability: AccessRule,
    },
}
