use crate::types::*;

/// Stores the event schema of all of the blueprints in a given package. In this map, the key is the
/// blueprint name and the value is another map which maps the schema hash to a local type index and
/// the schema.
#[derive(Debug, Clone, PartialEq, Eq, ScryptoSbor, Default)]
pub struct PackageEventSchemaSubstate(
    pub BTreeMap<String, BTreeMap<Hash, (LocalTypeIndex, Schema<ScryptoCustomTypeExtension>)>>,
);
