use crate::errors::*;
use crate::kernel::kernel_api::{KernelNodeApi, KernelSubstateApi};
use crate::system::kernel_modules::costing::FIXED_LOW_FEE;
use crate::types::*;
use native_sdk::runtime::Runtime;
use radix_engine_interface::api::node_modules::metadata::*;
use radix_engine_interface::api::substate_api::LockFlags;
use radix_engine_interface::api::types::{RENodeId, SubstateOffset};
use radix_engine_interface::api::unsafe_api::ClientCostingReason;
use radix_engine_interface::api::ClientApi;
use radix_engine_interface::blueprints::resource::{AccessRule, FnKey};

use super::SetMetadataEvent;

#[derive(Debug, Clone, Eq, PartialEq, ScryptoSbor)]
pub enum MetadataPanicError {
    ValueDecodeError(DecodeError),
}

pub struct MetadataNativePackage;
impl MetadataNativePackage {
    pub fn function_access_rules() -> BTreeMap<FnKey, AccessRule> {
        let mut access_rules = BTreeMap::new();
        access_rules.insert(
            FnKey::new(
                METADATA_BLUEPRINT.to_string(),
                METADATA_CREATE_IDENT.to_string(),
            ),
            rule!(allow_all),
        );
        access_rules.insert(
            FnKey::new(
                METADATA_BLUEPRINT.to_string(),
                METADATA_CREATE_WITH_DATA_IDENT.to_string(),
            ),
            rule!(allow_all),
        );
        access_rules
    }

    pub fn invoke_export<Y>(
        export_name: &str,
        receiver: Option<RENodeId>,
        input: ScryptoValue,
        api: &mut Y,
    ) -> Result<IndexedScryptoValue, RuntimeError>
    where
        Y: KernelNodeApi + KernelSubstateApi + ClientApi<RuntimeError>,
    {
        match export_name {
            METADATA_CREATE_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                if receiver.is_some() {
                    return Err(RuntimeError::InterpreterError(
                        InterpreterError::NativeUnexpectedReceiver(export_name.to_string()),
                    ));
                }

                let _input: MetadataCreateInput = scrypto_decode(&scrypto_encode(&input).unwrap())
                    .map_err(|e| {
                        RuntimeError::InterpreterError(InterpreterError::ScryptoInputDecodeError(e))
                    })?;

                let rtn = Self::create(api)?;

                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            METADATA_CREATE_WITH_DATA_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                if receiver.is_some() {
                    return Err(RuntimeError::InterpreterError(
                        InterpreterError::NativeUnexpectedReceiver(export_name.to_string()),
                    ));
                }

                let input: MetadataCreateWithDataInput =
                    scrypto_decode(&scrypto_encode(&input).unwrap()).map_err(|e| {
                        RuntimeError::InterpreterError(InterpreterError::ScryptoInputDecodeError(e))
                    })?;

                let rtn = Self::create_with_data(input.data, api)?;

                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            METADATA_SET_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                let receiver = receiver.ok_or(RuntimeError::InterpreterError(
                    InterpreterError::NativeExpectedReceiver(export_name.to_string()),
                ))?;

                let input: MetadataSetInput = scrypto_decode(&scrypto_encode(&input).unwrap())
                    .map_err(|e| {
                        RuntimeError::InterpreterError(InterpreterError::ScryptoInputDecodeError(e))
                    })?;

                let rtn = Self::set(receiver, input.key, input.value, api)?;

                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            METADATA_GET_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                let receiver = receiver.ok_or(RuntimeError::InterpreterError(
                    InterpreterError::NativeExpectedReceiver(export_name.to_string()),
                ))?;

                let input: MetadataGetInput = scrypto_decode(&scrypto_encode(&input).unwrap())
                    .map_err(|e| {
                        RuntimeError::InterpreterError(InterpreterError::ScryptoInputDecodeError(e))
                    })?;

                let rtn = Self::get(receiver, input.key, api)?;

                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            METADATA_REMOVE_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                let receiver = receiver.ok_or(RuntimeError::InterpreterError(
                    InterpreterError::NativeExpectedReceiver(export_name.to_string()),
                ))?;

                let input: MetadataRemoveInput = scrypto_decode(&scrypto_encode(&input).unwrap())
                    .map_err(|e| {
                    RuntimeError::InterpreterError(InterpreterError::ScryptoInputDecodeError(e))
                })?;

                let rtn = Self::remove(receiver, input.key, api)?;
                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            _ => Err(RuntimeError::InterpreterError(
                InterpreterError::NativeExportDoesNotExist(export_name.to_string()),
            )),
        }
    }

    pub(crate) fn create<Y>(api: &mut Y) -> Result<Own, RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        let object_id = api.new_object(METADATA_BLUEPRINT, vec![])?;

        Ok(Own::Object(object_id))
    }

    pub(crate) fn create_with_data<Y>(
        data: BTreeMap<String, String>,
        api: &mut Y,
    ) -> Result<Own, RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        let object_id = api.new_object(METADATA_BLUEPRINT, vec![])?;

        for (key, value) in data {
            Self::set(
                RENodeId::Object(object_id),
                key,
                scrypto_decode(
                    &scrypto_encode(&MetadataEntry::Value(MetadataValue::String(value))).unwrap(),
                )
                .unwrap(),
                api,
            )?;
        }

        Ok(Own::Object(object_id))
    }

    pub(crate) fn set<Y>(
        receiver: RENodeId,
        key: String,
        value: ScryptoValue,
        api: &mut Y,
    ) -> Result<(), RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        // Schema check
        // TODO: Remove this encode/decode mess
        let metadata: MetadataEntry =
            scrypto_decode(&scrypto_encode(&value).unwrap()).map_err(|e| {
                RuntimeError::ApplicationError(ApplicationError::MetadataError(
                    MetadataPanicError::ValueDecodeError(e),
                ))
            })?;

        let handle = api.sys_lock_substate(
            receiver,
            SubstateOffset::KeyValueStore(KeyValueStoreOffset::Entry(
                scrypto_encode(&key).unwrap(),
            )),
            LockFlags::MUTABLE,
        )?;

        let substate = Option::Some(value);
        api.sys_write_substate(handle, scrypto_encode(&substate).unwrap())?;
        api.sys_drop_lock(handle)?;

        Runtime::emit_event(
            api,
            SetMetadataEvent {
                key,
                value: metadata,
            },
        )?;

        Ok(())
    }

    pub(crate) fn get<Y>(
        receiver: RENodeId,
        key: String,
        api: &mut Y,
    ) -> Result<Option<MetadataEntry>, RuntimeError>
    where
        Y: KernelNodeApi + KernelSubstateApi + ClientApi<RuntimeError>,
    {
        let handle = api.sys_lock_substate(
            receiver,
            SubstateOffset::KeyValueStore(KeyValueStoreOffset::Entry(
                scrypto_encode(&key).unwrap(),
            )),
            LockFlags::read_only(),
        )?;

        let data = api.sys_read_substate(handle)?;
        let substate: Option<ScryptoValue> = scrypto_decode(&data).unwrap();

        let rtn = match substate {
            Option::Some(.., value) => {
                // TODO: Remove this encode/decode mess
                let metadata_value: MetadataEntry =
                    scrypto_decode(&scrypto_encode(&value).unwrap()).unwrap();
                Some(metadata_value)
            }
            Option::None => None,
        };

        Ok(rtn)
    }

    pub(crate) fn remove<Y>(
        receiver: RENodeId,
        key: String,
        api: &mut Y,
    ) -> Result<bool, RuntimeError>
    where
        Y: KernelNodeApi + KernelSubstateApi + ClientApi<RuntimeError>,
    {
        let handle = api.sys_lock_substate(
            receiver,
            SubstateOffset::KeyValueStore(KeyValueStoreOffset::Entry(
                scrypto_encode(&key).unwrap(),
            )),
            LockFlags::MUTABLE,
        )?;

        let data = api.sys_read_substate(handle)?;
        let substate: Option<ScryptoValue> = scrypto_decode(&data).unwrap();

        let rtn = match substate {
            Option::Some(..) => true,
            Option::None => false,
        };

        let substate: Option<ScryptoValue> = None;

        api.sys_write_substate(handle, scrypto_encode(&substate).unwrap())?;
        api.sys_drop_lock(handle)?;

        Ok(rtn)
    }
}
