pub struct RoyaltyNativePackage;
impl RoyaltyNativePackage {
    pub fn definition() -> PackageDefinition {
        let mut aggregator = TypeAggregator::<ScryptoCustomTypeKind>::new();

        let mut fields = Vec::new();
        fields.push(FieldSchema::static_field(
            aggregator.add_child_type_and_descendents::<ComponentRoyaltySubstate>(),
        ));

        let mut collections = Vec::new();
        collections.push(BlueprintCollectionSchema::KeyValueStore(
            BlueprintKeyValueStoreSchema {
                key: TypeRef::Static(aggregator.add_child_type_and_descendents::<String>()),
                value: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<RoyaltyAmount>(),
                ),
                can_own: false,
            },
        ));

        let mut functions = BTreeMap::new();
        functions.insert(
            COMPONENT_ROYALTY_CREATE_IDENT.to_string(),
            FunctionSchemaInit {
                receiver: None,
                input: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentRoyaltyCreateInput>(),
                ),
                output: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentRoyaltyCreateOutput>(),
                ),
                export: COMPONENT_ROYALTY_CREATE_IDENT.to_string(),
            },
        );
        functions.insert(
            COMPONENT_ROYALTY_SET_ROYALTY_IDENT.to_string(),
            FunctionSchemaInit {
                receiver: Some(schema::ReceiverInfo::normal_ref_mut()),
                input: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentSetRoyaltyInput>(),
                ),
                output: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentSetRoyaltyOutput>(),
                ),
                export: COMPONENT_ROYALTY_SET_ROYALTY_IDENT.to_string(),
            },
        );
        functions.insert(
            COMPONENT_ROYALTY_CLAIM_ROYALTIES_IDENT.to_string(),
            FunctionSchemaInit {
                receiver: Some(schema::ReceiverInfo::normal_ref_mut()),
                input: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentClaimRoyaltiesInput>(),
                ),
                output: TypeRef::Static(
                    aggregator.add_child_type_and_descendents::<ComponentClaimRoyaltiesOutput>(),
                ),
                export: COMPONENT_ROYALTY_CLAIM_ROYALTIES_IDENT.to_string(),
            },
        );

        let schema = generate_full_schema(aggregator);

        let blueprints = btreemap!(
            COMPONENT_ROYALTY_BLUEPRINT.to_string() => BlueprintDefinitionInit {
                blueprint_type: BlueprintType::default(),
                feature_set: btreeset!(),
                dependencies: btreeset!(RADIX_TOKEN.into(),),

                schema: BlueprintSchemaInit {
                    generics: vec![],
                    schema,
                    state: BlueprintStateSchemaInit {
                        fields,
                        collections,
                    },
                    events: BlueprintEventSchemaInit::default(),
                    functions: BlueprintFunctionsSchemaInit {
                        virtual_lazy_load_functions: btreemap!(),
                        functions,
                    },
                },

                royalty_config: PackageRoyaltyConfig::default(),
                auth_config: AuthConfig {
                    function_auth: btreemap!(
                        COMPONENT_ROYALTY_CREATE_IDENT.to_string() => rule!(allow_all),
                    ),
                    method_auth: MethodAuthTemplate::Static(
                        roles_template!(
                            roles {
                                COMPONENT_ROYALTY_ADMIN_ROLE;
                            },
                            methods {
                                COMPONENT_ROYALTY_CLAIM_ROYALTIES_IDENT => [COMPONENT_ROYALTY_ADMIN_ROLE];
                                COMPONENT_ROYALTY_SET_ROYALTY_IDENT => [COMPONENT_ROYALTY_ADMIN_ROLE];
                            }
                        ),
                    ),
                },
            },
        );

        PackageDefinition { blueprints }
    }

    pub fn invoke_export<Y>(
        export_name: &str,
        input: &IndexedScryptoValue,
        api: &mut Y,
    ) -> Result<IndexedScryptoValue, RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        match export_name {
            COMPONENT_ROYALTY_CREATE_IDENT => {
                api.consume_cost_units(FIXED_LOW_FEE, ClientCostingReason::RunNative)?;

                let input: ComponentRoyaltyCreateInput = input.as_typed().map_err(|e| {
                    RuntimeError::ApplicationError(ApplicationError::InputDecodeError(e))
                })?;
                let rtn = ComponentRoyaltyBlueprint::create(input.royalty_config, api)?;
                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            COMPONENT_ROYALTY_SET_ROYALTY_IDENT => {
                api.consume_cost_units(FIXED_MEDIUM_FEE, ClientCostingReason::RunNative)?;

                let input: ComponentSetRoyaltyInput = input.as_typed().map_err(|e| {
                    RuntimeError::ApplicationError(ApplicationError::InputDecodeError(e))
                })?;
                let rtn = ComponentRoyaltyBlueprint::set_royalty(input.method, input.amount, api)?;

                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            COMPONENT_ROYALTY_CLAIM_ROYALTIES_IDENT => {
                api.consume_cost_units(FIXED_MEDIUM_FEE, ClientCostingReason::RunNative)?;

                let _input: ComponentClaimRoyaltiesInput = input.as_typed().map_err(|e| {
                    RuntimeError::ApplicationError(ApplicationError::InputDecodeError(e))
                })?;
                let rtn = ComponentRoyaltyBlueprint::claim_royalties(api)?;
                Ok(IndexedScryptoValue::from_typed(&rtn))
            }
            _ => Err(RuntimeError::ApplicationError(
                ApplicationError::ExportDoesNotExist(export_name.to_string()),
            )),
        }
    }
}

#[derive(Debug, Clone, Eq, PartialEq, ScryptoSbor)]
pub enum ComponentRoyaltyError {
    ComponentRoyaltyIsDisabled,
}

pub struct ComponentRoyaltyBlueprint;

impl ComponentRoyaltyBlueprint {
    pub(crate) fn create<Y>(
        royalty_config: ComponentRoyaltyConfig,
        api: &mut Y,
    ) -> Result<Own, RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        // Create a royalty vault
        let accumulator_substate = ComponentRoyaltySubstate {
            enabled: matches!(royalty_config, ComponentRoyaltyConfig::Enabled(..)),
            royalty_vault: Vault::create(RADIX_TOKEN, api)?,
        };

        let mut kv_entries = BTreeMap::new();
        if let ComponentRoyaltyConfig::Enabled(royalty_amounts) = royalty_config {
            let mut royalty_config_entries = BTreeMap::new();
            for (method, (amount, frozen)) in royalty_amounts {
                royalty_config_entries.insert(
                    scrypto_encode(&method).unwrap(),
                    (scrypto_encode(&amount).unwrap(), frozen),
                );
            }
            kv_entries.insert(0u8, royalty_config_entries);
        }

        let component_id = api.new_object(
            COMPONENT_ROYALTY_BLUEPRINT,
            vec![],
            None,
            vec![scrypto_encode(&accumulator_substate).unwrap()],
            kv_entries,
        )?;

        Ok(Own(component_id))
    }

    pub(crate) fn set_royalty<Y>(
        method: String,
        amount: RoyaltyAmount,
        api: &mut Y,
    ) -> Result<(), RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        let accumulator_handle = api.actor_lock_field(
            OBJECT_HANDLE_SELF,
            RoyaltyField::RoyaltyAccumulator.into(),
            LockFlags::read_only(),
        )?;
        let component_royalty: ComponentRoyaltySubstate =
            api.field_lock_read_typed(accumulator_handle)?;
        if !component_royalty.enabled {
            return Err(RuntimeError::ApplicationError(
                ApplicationError::ComponentRoyaltyError(
                    ComponentRoyaltyError::ComponentRoyaltyIsDisabled,
                ),
            ));
        }
        api.field_lock_release(accumulator_handle)?;

        let handle = api.actor_lock_key_value_entry(
            OBJECT_HANDLE_SELF,
            0u8,
            &scrypto_encode(&method).unwrap(),
            LockFlags::MUTABLE,
        )?;
        api.key_value_entry_set_typed(handle, amount)?;
        api.key_value_entry_release(handle)?;

        Ok(())
    }

    pub(crate) fn claim_royalties<Y>(api: &mut Y) -> Result<Bucket, RuntimeError>
    where
        Y: ClientApi<RuntimeError>,
    {
        let handle = api.actor_lock_field(
            OBJECT_HANDLE_SELF,
            RoyaltyField::RoyaltyAccumulator.into(),
            LockFlags::read_only(),
        )?;

        let substate: ComponentRoyaltySubstate = api.field_lock_read_typed(handle)?;
        let mut royalty_vault = substate.royalty_vault;
        let bucket = royalty_vault.take_all(api)?;
        api.field_lock_release(handle)?;

        Ok(bucket)
    }

    pub fn charge_component_royalty<Y, V>(
        receiver: &NodeId,
        ident: &str,
        api: &mut Y,
    ) -> Result<(), RuntimeError>
    where
        V: SystemCallbackObject,
        Y: KernelApi<SystemConfig<V>>,
    {
        let accumulator_handle = api.kernel_lock_substate(
            receiver,
            ROYALTY_BASE_PARTITION
                .at_offset(ROYALTY_FIELDS_PARTITION_OFFSET)
                .unwrap(),
            &RoyaltyField::RoyaltyAccumulator.into(),
            LockFlags::read_only(),
            SystemLockData::default(),
        )?;
        let component_royalty: ComponentRoyaltySubstate = api
            .kernel_read_substate(accumulator_handle)?
            .as_typed()
            .unwrap();
        if !component_royalty.enabled {
            api.kernel_drop_lock(accumulator_handle)?;
            return Ok(());
        }

        let royalty_charge = {
            let handle = api.kernel_lock_substate_with_default(
                receiver,
                ROYALTY_BASE_PARTITION
                    .at_offset(ROYALTY_CONFIG_PARTITION_OFFSET)
                    .unwrap(),
                &SubstateKey::Map(scrypto_encode(ident).unwrap()),
                LockFlags::read_only(),
                Some(|| {
                    let kv_entry = KeyValueEntrySubstate::<()>::default();
                    IndexedScryptoValue::from_typed(&kv_entry)
                }),
                SystemLockData::default(),
            )?;

            let substate: KeyValueEntrySubstate<RoyaltyAmount> =
                api.kernel_read_substate(handle)?.as_typed().unwrap();
            api.kernel_drop_lock(handle)?;
            substate.value.unwrap_or(RoyaltyAmount::Free)
        };

        if royalty_charge.is_non_zero() {
            let vault_id = component_royalty.royalty_vault.0;
            let component_address = ComponentAddress::new_or_panic(receiver.0);

            apply_royalty_cost(
                api,
                royalty_charge,
                RoyaltyRecipient::Component(component_address),
                vault_id.into(),
            )?;
        }

        api.kernel_drop_lock(accumulator_handle)?;

        Ok(())
    }
}
